            left: - slideWidth
        }, 1000, function () {
            $('#slideshow8 ul li:first-child').appendTo('#slideshow8 ul');
            $('#slideshow8 ul').css('left', '');
        });
    };

    $('#prev_btn').click(function () {
        moveLeft();
    });

    $('#next_btn').click(function () {
        moveRight();
