 let selectedItemIndex = -1;
let clearSelectionTimeout;

document.addEventListener("keydown", function(event) {
    const countryList = document.getElementById("country-list").getElementsByTagName("li");

    if (event.key === "ArrowDown") {
        selectedItemIndex = Math.min(selectedItemIndex + 1, countryList.length - 1);
        updateSelection();
    } else if (event.key === "ArrowUp") {
        selectedItemIndex = Math.max(selectedItemIndex - 1, 0);
        updateSelection();
    }
});

function updateSelection() {
    clearTimeout(clearSelectionTimeout); // Clear any existing timeout

    const countryList = document.getElementById("country-list").getElementsByTagName("li");
    for (let i = 0; i < countryList.length; i++) {
        if (i === selectedItemIndex) {
            countryList[i].classList.add("selected");
        } else {
            countryList[i].classList.remove("selected");
        }
    }

    // Set a timeout to clear the selection after a delay (e.g., 1 second)
    clearSelectionTimeout = setTimeout(() => {
        clearSelection();
    }, 1000);
}

function clearSelection() {
    const countryList = document.getElementById("country-list").getElementsByTagName("li");
    for (let i = 0; i < countryList.length; i++) {
        countryList[i].classList.remove("selected");
    }
}

</script>
