}


function notEmpty(elem, helperMsg, getid){
if(elem.value.length == 0){
// alert(helperMsg);
document.getElementById(getid).innerHTML = helperMsg;
elem.focus(); // set the focus to this input
return false;
}
document.getElementById(getid).style.display='none';
return true;
}

function isNumeric(elem, helperMsg, getid){
var numericExpression = /^[0-9]+$/;
if(elem.value.match(numericExpression)){
document.getElementById(getid).style.display='none';
return true;
}else{
// alert(helperMsg);
document.getElementById(getid).innerHTML = helperMsg;
elem.focus();
return false;
}
}

function isAlphabet(elem, helperMsg, getid){
var alphaExp = /^[a-zA-Z ]+$/;
if(elem.value.match(alphaExp)){
document.getElementById(getid).style.display='none';
return true;
}else{
// alert(helperMsg);
document.getElementById(getid).innerHTML = helperMsg;
elem.focus();
return false;
}
}

function isAlphanumeric(elem, helperMsg, getid){
var alphaExp = /^[0-9a-zA-Z ,]+$/;
if(elem.value.match(alphaExp)){
document.getElementById(getid).style.display='none';
return true;
}else{
// alert(helperMsg);
document.getElementById(getid).innerHTML = helperMsg;
elem.focus();
return false;
}
}

function lengthRestriction(elem, min, max){
var uInput = elem.value;
if(uInput.length >= min && uInput.length <= max){
return true;
}else{
alert("Please enter between " +min+ " and " +max+ " characters");
elem.focus();
return false;
}
}

function madeSelection(elem, helperMsg, getid){
if(elem.value == "Please Choose"){
// alert(helperMsg);
document.getElementById(getid).innerHTML = helperMsg;
elem.focus();
return false;
}else{
document.getElementById(getid).style.display='none';
return true;

}
}

function emailValidator(elem, helperMsg, getid){
var emailExp = /^[w-.+]+@[a-zA-Z0-9.-]+.[a-zA-z0-9]{2,4}$/;
if(elem.value.match(emailExp)){
document.getElementById(getid).style.display='none';
return true;
}else{
// alert(helperMsg);
document.getElementById(getid).innerHTML = helperMsg;
elem.focus();
return false;
}
}



