  $(document).ready(function() {
  // hide both divs initially
  $("#div<?php echo $k; ?>").hide();
  $("#divv<?php echo $k; ?>").hide();

  // when the dropdown changes, check the selected option and show/hide divs accordingly
  $("#myselection<?php echo $k; ?>").change(function() {
    var val = $(this).val();

    if (val === "option<?php echo $k; ?>") {
      $("#div<?php echo $k; ?>").show();
      $("#divv<?php echo $k; ?>").hide();
    } else if (val === "optionn<?php echo $k; ?>") {
      $("#div<?php echo $k; ?>").hide();
      $("#divv<?php echo $k; ?>").show();
    } else {
      // if neither condition is met, hide both divs
      $("#div<?php echo $k; ?>").hide();
      $("#divv<?php echo $k; ?>").hide();
    }
  });
});

          
