        <script>
            $(document).ready(function() {
                // Find all slick-news-item elements
                var newsItems = $(".slick-news-item");
                if (newsItems.length > 0) {
                    // Find the maximum height among news items
                    var maxHeight = 0;
                    newsItems.each(function() {
                        var itemHeight = $(this).height();
                        if (itemHeight > maxHeight) {
                            maxHeight = itemHeight;
                        }
                    });

                    // Set the maximum height to all news items
                    newsItems.height(maxHeight);
                }
            });
        </script>


