	<script type="text/javascript">

		function validatebook() {

			var flag = true;


			
			var error = "";
			var enqname = document.getElementById("enqnames");
			if (enqname.value == "") {
				error = "Enter Your Name ";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}

			if (flag) {
				var x = document.getElementById('mobile').value;
				if (isNaN(x) || x.indexOf(" ") != -1) {
					error = "Enter Numeric values Only ";
					document.getElementById("error_pararf").innerHTML = error;
					return false;
				}

				if (!(x.length == 10 || x.length == 8)) {
					error = "Enter Your Mobile No. ";
					document.getElementById("error_pararfs").innerHTML = error;
					return false;

				}

			}

			if (flag) {
				var reg = /^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/;
				var address = document.getElementById('enqemails').value;
				if (reg.test(address) == false) {

					error = "Enter Your Email Address ";
					document.getElementById("error_pararfs").innerHTML = error;
					return false;
				}
			}


			var error = "";
			var roomss = document.getElementById("inputState1");
			if (roomss.value == "") {
				error = "Select no. of Rooms";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}

			var error = "";
			var adult = document.getElementById("inputState2");
			if (adult.value == "") {
				error = "Select no. of Adult";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}

			var error = "";
			var child = document.getElementById("inputState3");
			if (child.value == "") {
				error = "Select no. of Child";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}

			

			var error = "";
			var datetimepicker90 = document.getElementById("datetimepicker90");
			if (datetimepicker90.value == "") {
				error = "Check In Date";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}

			var error = "";
			var datetimepicker91 = document.getElementById("datetimepicker91");
			if (datetimepicker91.value == "") {
				error = "Check Out Date";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}

			

			var error = "";
			var captcha = document.getElementById("captcha");
			if (captcha.value == "") {
				error = "Enter Captcha Code";
				document.getElementById("error_pararfs").innerHTML = error;
				return false;
			}
			else {
				return true;
			}

		}

	</script>
