    <script>
    var content = $('.content'),
        form = $('.form'),
        message = $('#message'),
        animatable = $('.animatable'),
        signedIn = 'signed-in',
        signedUp = 'signed-up';

    //Toggle between forms
    $('.sign--type .link').click(function() {
        content.hide();
        if ($(this).hasClass('signin')) {
            $('.signin--content').show();
        } else if ($(this).hasClass('signup')) {
            $('.signup--content').show();
        }
    });

    //On form submit
    $('form').submit(function() {
        sendForm($(this).attr('id'));
        return false;
    });

    //Animate form and wait for reset
    function sendForm(form) {
        setTimeout(function() {
            animateForm(form);
        }, 200);
        setTimeout(reset, 5000);
    }

    function animateForm(frm) {
        animatable.addClass(frm);
        content.hide(300);

        //check which form to call appropriate animation
        if (frm == signedIn) {
            message.text("welcome back");
        } else if (frm == signedUp) {
            message.text("welcome to")
            $('.container').addClass(signedUp);
        }
    }

    //reset form
    function reset() {
        content.hide();
        if (form.hasClass(signedIn)) {
            $('.signin--content').show();
            animatable.removeClass(signedIn);
        }
        if (form.hasClass(signedUp)) {
            $('.signup--content').show();
            animatable.removeClass(signedUp);
            $('.container').removeClass(signedUp);
        }
        message.text(null);
    }
    </script>


    <script>
    var counted = 0;
    $(window).scroll(function() {

        var oTop = $('#counter').offset().top - window.innerHeight;
        if (counted == 0 && $(window).scrollTop() > oTop) {
            $('.count').each(function() {
                var $this = $(this),
                    countTo = $this.attr('data-count');
                $({
                    countNum: $this.text()
                }).animate({
                        countNum: countTo
                    },

                    {

                        duration: 2000,
                        easing: 'swing',
                        step: function() {
                            $this.text(Math.floor(this.countNum));
                        },
                        complete: function() {
                            $this.text(this.countNum);
                            //alert('finished');
                        }

                    });
            });
            counted = 1;
        }

    });
    </script>
  